mapper.select:
{
    #{listResult, jdbcType=OTHER, mode=OUT, resultMap=listResult, javaType=java.sql.ResultSet}
    =
    call public.f_list_posts(
         #{offset, jdbcType=INTEGER, mode=IN}
    )
}

{
    #{listResultProcessed, jdbcType=OTHER, mode=OUT, resultMap=listResultProcessed, javaType=java.sql.ResultSet}
    =
    call public.f_list_posts_processed(
      #{pid_post_source, jdbcType=INTEGER, mode=IN},
      #{pmin_similarity, jdbcType=NUMERIC, mode=IN}
      #{pmax_results,    jdbcType=INTEGER, mode=IN}
    )
}

{
    #{listResultNotProcessed, jdbcType=OTHER, mode=OUT, resultMap=listResultNotProcessed, javaType=java.sql.ResultSet}
    =
    call public.f_list_posts_not_processed(
      #{postId, jdbcType=INTEGER, mode=IN},
      #{pageSize, jdbcType=INTEGER, mode=IN}
    )
}

{
    #{postId, jdbcType=INTEGER, mode=OUT, javaType=java.lang.Integer, }
    =
    call f_ins_post_processed(
        #{pid_post,               jdbcType=INTEGER, mode=IN, javaType=java.lang.Integer},
        #{pid_post_associated,    jdbcType=INTEGER, mode=IN, javaType=java.lang.Integer},
        #{pvl_similarity,         jdbcType=NUMERIC,  mode=IN, javaType=java.lang.Double}
    )
}
